import { ThrowNoJsonLoaderError } from './config';

import oCountries from '../../static/countries.json';

if (process.env.NODE_ENV !== 'production') {
  if (!oCountries || Object.keys(oCountries).length === 0) {
    ThrowNoJsonLoaderError();
  }
}

export var getCountries = function getCountries() {
  return oCountries;
};
export var getCountryCodes = function getCountryCodes() {
  return getCountries().map(function (o) {
    return o.code;
  });
};